rc=\"<?= theme_resource_url(\'images/btn_order_review.gif\') ?>\" alt=\"Next\" onclick=\"return $(this).getForm().sendRequest(\'create_local_order\')\"/>\n    </form>\n  </div>\n  <div class=\"clear\"></div>\n<? else: ?>\n  <p>Your shopping cart is empty.</p>\n  <p><a href=\"<?= root_url(\'/\') ?>\"><img src=\"<?= theme_resource_url(\'images/btn_continue_shopping.gif\') ?>\" alt=\"Continue shopping\"/></a></p>\n<? endif ?>',NULL,NULL,'2012-05-22 18:28:17','2012-05-22 18:28:17',2,'shop:checkout_billing_info',NULL,'function create_local_order($controller, $page, $params)\n{\n  if (!Shop_Cart::list_active_items())\n    throw new Exception(\'Your cart is empty!\');\n \n  $controller->exec_action_handler(\'shop:on_checkoutSetBillingInfo\');\n  Shop_CheckoutData::copy_billing_to_shipping();\n  Shop_CheckoutData::set_payment_method(Shop_PaymentMethod::find_by_api_code(\'credit_card\')->id);\n  Shop_CheckoutData::set_shipping_method(Shop_ShippingOption::find_by_api_code(\'post_service\')->id);\n   \n  Phpr::$response->redirect(root_url(\'/checkout_order_preview\'));\n}','everyone',NULL,'any',0,'One-step checkout example',NULL,NULL,1,NULL,20,0,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,'one_step_checkout',1),(21,'Search','/search',NULL,NULL,'<h2>Product search</h2>\n\n<? $this->render_partial(\'shop:search_form\') ?>\n\n<? if (strlen($query)): ?>\n    <p>Products found: <?= $pagination->getRowCount() ?></p>\n\n    <? $this->render_partial(\'shop:product_list\', array(\'products\'=>$products, \'paginate\'=>false)) ?>\n\n    <? $this->render_partial(\'pagination\', array(\'pagination\'=>$pagination, \'base_url\'=>root_url(\'/search\'), \'suffix\'=>\'?query=\'.urlencode($query).\'&amp;records=\'.urlencode($records))) ?>\n<? endif ?>',NULL,NULL,'2012-05-22 18:28:17','2012-05-22 18:28:17',2,'shop:search',NULL,NULL,'everyone',NULL,'any',0,'The product search page','$this->data[\'no_search_form\'] = true;',NULL,1,NULL,21,0,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,