insert into system_email_templates(code, subject, content, description) values('blog:new_comment_notification', 'New Comment', '<p>Dear {comments_subscriber_name}!</p><p>New comment has been added to the LemonStand blog post {post_name_and_url}:</p><p>Author: {comment_author_name}<br />Comment:</p><blockquote>{comment_text}</blockquote><p>To unsubscribe please click the link: {comments_unsubscribe_link}</p>', 'This message is sent to visitors who have subscribed to a blog post new comments notifications.');